        e.target.innerText = "始ろう";
    }

    return(

        <div className="Intro-overlay">
            <audio className="kanjiAudio" autoPlay>
                    <source src={props.kanji[0].examples[rngExampleIndex].audio.mp3} type="audio/mp3"/>
                </audio>
            <audio src="https://open.spotify.com/embed/track/2Nho8rhto2Lxxoc7Eulo2g?utm_source=generator" allow="encrypted-media" autoPlay></audio>
            <h1 className="intro-1">KANJI</h1>
            <h1 className="intro-2">DEATHMATCH</h1>
            <h1 className="intro-3">漢字のデスマッチ</h1>
            <Link onMouseOver={buttonHover} to="/home"><button className="intro-4">begin</button></Link>
      </div>
    )
}

export default Intro;
