import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from "react-router-dom";


const KanjiMain = () => {

    const playSplat1 = () => {
        const sound = document.createElement("audio");
        const soundSrc = document.createElement("source");
        soundSrc.src = "./KDM-splat-1.m4a";
        soundSrc.type = "audio/m4a"
        document.querySelector(".Home").append(sound);
        sound.append(soundSrc);
        sound.load();
        sound.play();
    }

    return (
        <div onClick={playSplat1}>
            Kanji Main Page
        </div>
    )
}

export default KanjiMain;
