import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from "react-router-dom";
import KanjiMain from "./KanjiMain";


const Home = () => {

    const [kanji, setKanji] = useState([]);
    const [searchInfo, setSearchInfo] = useState("")

    const options = {
    method: 'GET',
    headers: {
      'X-RapidAPI-Host': 'kanjialive-api.p.rapidapi.com',
      'X-RapidAPI-Key': '58f5ec4060msh8e547825688a507p19b380jsn8306895f50db'
    }
  };
  useEffect(() => {
    fetch('https://kanjialive-api.p.rapidapi.com/api/public/search/advanced/?kem=pig', options)
    .then(response => response.json())
    .then(data => {
      setKanji(data);
      setPlayer1(data[0].radical.order);
      setPlayer2(data[0].radical.order);})
    .catch(err => console.error(err));  
  }, [])

    const handleChange = (e) => {
        console.log("input");
        setSearchInfo(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("submit");
    }

    return (
        <div className="Home">
            <header>
                <nav>
                    <h1>Kanji Deathmatch</h1>
                    <ul>
                        <li>About</li>
                    </ul>
                </nav>
            </header>
            <div>
               <form onSubmit={handleSubmit}>
                   <label></label>
                   <input onChange={handleChange} value={searchInfo} type="text" placeholder="Type some stuff..."></input>
                   <button type="submit">Kanji-fy!</button>
               </form>
            </div>
            <div>
                <Routes>
                    <Route path="/kanji" element={<KanjiMain />} />
                </Routes>
            </div>
        </div>
    )
}

export default Home;
