import { useState } from 'react';
import Header from '../Components/Header';

const Fight = (props) => {
console.log(props)
    //Assign battle stats to each kanji based on data

    let p1 = props.fighters[0];
    let p2 = props.fighters[1];
    const [player1Hp, setPlayer1Hp] = useState((p1.kanji.strokes.count * p1.radical.strokes) ** 2);
    const [player2Hp, setPlayer2Hp] = useState((p2.kanji.strokes.count * p2.radical.strokes) ** 2)

    // p1.attackPower = p1 / 100;
    // p2.attackPower = p2 / 100;
    let gameClock;
    // console.log(p1.attackPower, p2.attackPower);

    const handleBattle = () => {
        console.log("here");
        let round = 1;
        let rngAttack;
        let p1HpCopy = player1Hp;
        let p2HpCopy = player2Hp
        gameClock = setInterval(() => {
            if (player1Hp > 0 && player2Hp > 0) {
                switch (round % 2 === 0) {
                    case true :
                        rngAttack = Math.floor(Math.random() * p1.attackPower);
                        p2HpCopy -= rngAttack;
                        setPlayer2Hp(p2HpCopy);
                        console.log(`p1 attacks for: ${rngAttack} damage!`, player2Hp);
                        break;
                    case false :
                        rngAttack = Math.floor(Math.random() * p2.attackPower);
                        p1HpCopy -= rngAttack;
                        setPlayer1Hp(p1HpCopy);
                        console.log(`p2 attacks for: ${rngAttack} damage!`, player1Hp);
                        break;
                }
                round++;
                console.log(round)
            }

        }, 3000)
    }

    setTimeout(handleBattle, 3000)

    return (
        <div className="Dojo">
            <header className="battleStats">
                <div className="player-1-hp"><h1>{p1.hp}</h1></div>
                <div className="logo"></div>
                <div className="player-2-hp"><h1>{p2.hp}</h1></div>
            </header>
            <section className="battle-stats">
                <div></div>
                <div></div>
            </section>
        </div>
    )
}

export default Fight;
