import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from "react-router-dom";


const KanjiMain = () => {

    const playSplat = () => {
        let sound = document.querySelector("audio");
        sound.load();
        sound.play();
    }

    return (
        <div onClick={playSplat}>
            Kanji Main Page
            <audio src="./KDM-splat-1.mp3">
                <source src="./KDM-splat-1.mp3" type="audio/mp4"/>
            </audio>
        </div>
    )
}

export default KanjiMain;
